mapper.select:
update article
set view_count = view_count + 1
where article.id = #{articleId};
select article.id, article.title, article.content,
       article.created_time, article.view_count, article.comment_count,
       category.id as category_id, category.name, category.icon
from article,category
where article.id = #{articleId}
  and article.category_id = category.id;

select article.id, article.title, article.synopsis, article.created_time, article.view_count, article.comment_count, category.id as category_id, category.name, category.icon from article, category where article.category_id = category.id
and article.category_id = #{categoryId}
order by article.created_time desc
